mapper.select:
SELECT 	COUNT(*)
FROM 	tbl_member
WHERE 	userid = #{userid}

SELECT 	userid, passwd, name 
FROM 	tbl_member 
WHERE 	userid = #{userid} 
AND 	passwd = #{passwd}

mapper.update:
UPDATE 	tbl_member 
SET 	passwd = #{passwd},
		name   = #{name} 
WHERE 	userid = #{userid}

mapper.delete:
DELETE 
FROM tbl_member 
WHERE 	userid = #{userid} 
AND 	passwd = #{passwd}
